    bool write_it = !dryrun;
    size_t len = 20, skip = 0;

    if (segmentspec=="")
      return ShowError(msg+"segmentspec should be set, eg. segmentspec=sbd"
		       " or segmentspec=sbd1");

    if (query->Nindices()==0)
      return ShowError(msg+"features unspecified");

